(5, 1, 0, 'phoneForm', '', 0, 6, 0, '<form id=\"phone-form\" action=\"\">\n    <div class=\"form\">\n        <div class=\"phone_form\">\n            <div class=\"form__input\">\n                <input name=\"phone\" type=\"tel\" placeholder=\"(999) 999-99-99\" />\n            </div>\n            <button class=\"button button_primary\"> </button>\n        </div>\n        <a href=\"/\" class=\"text_body3 form_caption\">       .</a>\n    </div>\n</form>\n<script src=\"assets/js/libs/jquery.js\"></script> \n<script src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery-validate/1.19.5/jquery.validate.min.js\"></script>\n<script>\n    var formph = $(\'#phone-form\');	\n    formph.validate({\n    submitHandler: function(formph) {\n		var fd = new FormData( formph );\n		formph.reset();\n		$.ajax({\n			async: true,\n            url: \"assets/mail/phone_form.php\", \n            type: \"POST\",             \n            data: fd,\n            cache: false,  \n			contentType: false,			\n            processData: false,      \n            success: function(data) {\n				formph.reset();\n				document.getElementById(\'success\').hidden = false\n                setTimeout(() => {\n                  document.getElementById(\'success\').hidden = true  \n                }, 5000)\n            },\n			error: function(data){\n				console.log(\'error\');\n				console.log(data);\n				document.getElementById(\'error\').hidden = false\n                setTimeout(() => {\n                  document.getElementById(\'error\').hidden = true  \n                }, 5000)\n			}\n        });\n        return false;\n	  },\n	  rules: {\n	    phone: {\n			required: true\n		},\n	  },\n	 messages: {\n	    phone: {\n			required: \" !\"\n    	},\n	  },\n	    errorElement : \"div\",\n		focusInvalid: true,\n		errorClass: \"input_error\"\n	});\n</script>', 0, 'a:0:{}', 0, ''),
