data);\n				document.getElementById(\'modal\').classList.remove(\'open\')\n				document.getElementById(\'error\').hidden = false\n                setTimeout(() => {\n                  document.getElementById(\'error\').hidden = true  \n                }, 5000)\n			}\n        });\n        return false;\n	  },\n	  rules: {\n	    name: {\n	        required: true\n	    },\n	    phone: {\n			required: true\n		},\n	  },\n	 messages: {\n	     name: {\n	        required: \"  !\"\n	    },\n	    phone: {\n			required: \"  !\"\n    	},\n	  },\n	    errorElement : \"div\",\n		focusInvalid: true,\n		errorClass: \"input_error\"\n	});\n</script>', 0, 'a:0:{}', 0, ''),
